type: 'scatter',
symbolSize: function (value){
    // 我的思路是：把最大的气泡的大小设为图表宽度的十分之一
    // 所以这里需要获取所有数据系列中最大的气泡的大小和图表的宽度
    // 问题是每显示一个气泡都要重新获取一遍，对于有效率强迫症的我很心塞
    // 我的意思是，对于气泡图，symbolSize应该默认就是这种缩放方式，在echarts内部实现
    var max = 0, width = parseInt($(myChart.getDom()).css('width'));
    $.each(myChart.getSeries(), function(i, v){
        $.each(v.data, function(ii, vv){
            if (vv[2] > max) {
                max = vv[2];
            };
        });
    });
    var rate = width/10/max;

    return parseInt(value[2]*rate);
}
