require.config({
    paths: {
        angular: 'bower_components/angular/angular',
        angularRouter: 'bower_components/angular-ui-router/release/angular-ui-router',
        angularMocks: 'bower_components/angular-mocks/angular-mocks',
        text: 'bower_components/requirejs-text/text',
        'echarts': 'bower_components/baidu-echarts/build/echarts',
        'echarts/chart/bar': 'bower_components/baidu-echarts/build/echarts',
