zrender/tool/util.js
`function mergeItem(target, source, key, overwrite) {
            if (source.hasOwnProperty(key)) {
                var targetProp = target[key];
                if (typeof targetProp == 'object'
                    && !BUILTIN_OBJECT[objToString.call(targetProp)]
                    // 是否为 dom 对象
                    && !isDom(targetProp)
                ) {
                    // 如果目标属性为数组时
                    if (targetProp instanceof Array) {
                        if (overwrite){
                            target[key] = source[key];
                        }
                        else{
                            for (var i = 0, lens = source[key].length, lent = target[key].length; i < lens; i++) {
                                target[key][lent + i] = source[key][i];
                            }
                        }
                    }
                    // 如果需要递归覆盖，就递归调用merge
                    merge(
                        target[key],
                        source[key],
                        overwrite
                    );
                }
                else if (overwrite || !(key in target)) {
                    // 否则只处理overwrite为true，或者在目标对象中没有此属性的情况
                    target[key] = source[key];
                }
            }
        }`

