没有，现有图表做起来其实也蛮简单的，随便找个example试试这个option，随便写的，可以自己封装一下

var oridata = [
  [120, 132, 101, 134, 90, 230, 210],
  [220, 182, 191, 234, 290, 330, 310],
  [150, 232, 201, 154, 190, 330, 410]
];
var data0 = [];
var data1 = [];
var data2 = [];
var total;
for (var i = 0, l = oridata[0].length; i < l; i++) {
    total = oridata[0][i] + oridata[1][i] + oridata[2][i];
    data0.push(Math.round(oridata[0][i]/total \* 100));
    data1.push(Math.round(oridata[1][i]/total \* 100));
    data2.push(100 - data0[data0.length - 1] - data1[data1.length - 1]);
}
option = {
    tooltip : {
        trigger: 'axis',
        axisPointer : {            // 坐标轴指示器，坐标轴触发有效
            type : 'shadow'        // 默认为直线，可选为：'line' | 'shadow'
        }
    },
    legend: {
        data:['邮件营销','联盟广告','视频广告']
    },
    toolbox: {
        show : true,
        orient: 'vertical',
        x: 'right',
        y: 'center',
        feature : {
            mark : true,
            dataView : {readOnly: false},
            magicType:['line', 'bar'],
            restore : true,
            saveAsImage : true
        }
    },
    calculable : true,
    xAxis : [
        {
            type : 'category',
            data : ['周一','周二','周三','周四','周五','周六','周日']
        }
    ],
    yAxis : [
        {
          type : 'value',
            splitArea : {show : true}
        }
    ],
    series : [
        {
            name:'邮件营销',
            type:'bar',
            stack: '广告',
            data:data0
        },
        {
            name:'联盟广告',
            type:'bar',
            stack: '广告',
            data:data1
        },
        {
            name:'视频广告',
            type:'bar',
            stack: '广告',
            data:data2
        }
    ]
};

