    legend: {
        data: ['s']
    },
    tooltip: {
        formatter: function(param) {
            console.log(param);
            return '[' + param.value[0] + ', ' + param.value[1] +
                ']: ' + param.value[2];
        }
    },
    xAxis: {
        type: 'value'
