var update = function(){
    var model = myChart.getModel();
    var geo = model.getComponent('geo').coordinateSystem;
    var latlong = ["14.550072", "47.516231"];
    var xy = geo.dataToPoint(latlong);
    var grid = option.grid[0];

    myChart.setOption({
        grid: {
            left: xy[0],
            top: xy[1] - grid.height
        }
    });
}                    
