struct TestParam : public dmlc::Parameter<TestParam> {
  int c;
  DMLC_DECLARE_PARAMETER(TestParam) {
    DMLC_DECLARE_FIELD(c)
    .describe("test.");
  }
};
DMLC_REGISTER_PARAMETER(TestParam);

inline void Test(const nnvm::NodeAttrs& attrs,
                           const OpContext &ctx,
                           const std::vector<TBlob> &inputs,
                           const std::vector<OpReqType> &req,
                           const std::vector<TBlob> &outputs) {
  const auto& param = nnvm::get<TestParam>(attrs.parsed);
}

NNVM_REGISTER_OP(prof)
.set_num_inputs(1)
.set_num_outputs(1)
.set_attr_parser(op::ParamParser<TestParam>)
.attr<nnvm::FInferShape>("FInferShape", op::UniformShape)
.attr<nnvm::FInferType>("FInferType", op::UniformType)
.attr<FCompute>("FCompute<cpu>", Test)
.add_argument("src", "NDArray", "source")
.add_arguments(TestParam::__FIELDS__());

