  const char *keys[] = {"c", "c"};
  const char *vals[] = {"13", "12"};
  int N = 100000;
  clock_t tic = clock();
  for (int i = 0; i < N; ++i) {
    NDArray** outs = NULL;
    int nouts = 0;
    MXImperativeInvoke((void*)op,
                       1,
                       (void**)dmlc::BeginPtr(ins),
                       &nouts,
                       (void***)&outs,
                       1,
                       const_cast<char**>(keys),
                       const_cast<char**>(vals));
    (outs[0])->WaitToRead();
  }
  std::cout << static_cast<float>(clock()-tic)/CLOCKS_PER_SEC/N << std::endl;
  return 0;
}
