#ifdef **GNUG**
std::string OperationTimer::demangle(const char\* name) {
  int status = -4; // some arbitrary value to eliminate warning
  std::unique_ptr<char, void(*)(void *)> res {
    abi::__cxa_demangle(name, NULL, NULL, &status),
    std::free
  };
  return (status == 0) ? res.get() : name ;
}
#else
// does nothing if not g++
std::string OperationTimer::demangle(const char\* name) {
  return name;
}
#endif
