```
   m_params_map = NDArray::LoadToMap(fileNamePars);
   SplitParams();

   void MxNetwork::SplitParams() 
   {
   	m_args_map.clear();
   	m_auxs_map.clear();
   
   	int pos = 0;
   	for (auto it = m_params_map.begin(); it != m_params_map.end(); ++it)
   	{
   		const string key = it->first;
   		NDArray val = it->second;
   		Shape shape = Shape(val.GetShape());
   		if ((pos = key.find("arg:")) != string::npos)
   		{
   			m_args_map[key.substr(4)] = NDArray(shape, Context::gpu(), false);
   			val.CopyTo(&m_args_map[key.substr(4)]);
   		}
   		if ((pos = key.find("aux:")) != string::npos)
   		{
   			m_auxs_map[key.substr(4)] = NDArray(shape, Context::gpu(), false);
   			val.CopyTo(&m_auxs_map[key.substr(4)]);
   		}
  	}
   }
```
For SimpleBind call (not documented in c++ examples):
```
SimpleBind(Context::gpu(), m_args_map, std::map<std::string, NDArray>(), std::map<std::string, OpReqType>(), m_auxs_map);
```

