It's the mxnet example for the Data Science Bowl, except that I have customised the following part of the R script.

mx.callback.early.stop <- function(period, prefix, logger=NULL) {
  function(iteration, nbatch, env) {
    if (nbatch %% period == 0 && !is.null(env$metric)) {
          result <- env$metric$get(env$train.metric)
          if (nbatch != 0)
                cat(paste0("Batch [", nbatch, "] Train-", result$name, "=", result$value, "\n"))
          if (!is.null(logger)) {
                # save a copy of the parameters
                mx.model.save(env$model, prefix, iteration)
                cat(sprintf("Model checkpoint saved to %s-%04d.params\n", prefix, iteration))
                # log the results
                if (class(logger) != "mx.metric.logger") {
                      stop("Invalid mx.metric.logger.")
                }
                logger$train <- c(logger$train, result$value)
                if (!is.null(env$eval.metric)) {
                     result <- env$metric$get(env$eval.metric)
                    if (nbatch != 0)
                        cat(paste0("Batch [", nbatch, "] Validation-", result$name, "=", result$value, "\n"))
                    logger$eval <- c(logger$eval, result$value)
                    iMin = which.min(logger$eval)
                    iAll = length(logger$eval)
                    if (iAll - iMin >= period / 2)
                        cat("**\* Eval performance deteriorating\n")
                    if (iMin + period <= iAll) return(FALSE)
                }
          }
    }
    return(TRUE)
  }
}

set.seed(12345)
network <- get.lenet()
batch_size <- 16

# CSVIter is used here, since the data can't fit into memory

data_train <- mx.io.CSVIter(
  data.csv = file.path(workingPath, "local_train-64x64-data.csv"), data.shape = c(64, 64, 30),
  label.csv = file.path(workingPath, "local_train-stytole.csv"), label.shape = 600,
  batch.size = batch_size
)

data_test <- mx.io.CSVIter(
  data.csv = file.path(workingPath, "local_test-64x64-data.csv"), data.shape = c(64, 64, 30),
  label.csv = file.path(workingPath, "local_test-stytole.csv"), label.shape = 600,
  batch.size = batch_size
)

logger = mx.metric.logger()

mx.set.seed(0)
stytole_model <- mx.model.FeedForward.create(
    X = data_train,
    eval.data = data_test,
    ctx = mx.gpu(0),
    symbol = network,
    num.round = 5,
    learning.rate = 0.001,
    wd = 0.00001,
    momentum = 0.9,
    eval.metric = mx.metric.CRPS,
    epoch.end.callback = mx.callback.early.stop(8, paste0(workingPath, "/systole"), logger)
)

plot(1:length(logger$eval), logger$train, type = "l", col = "blue", main = "Systole Training Performance")
lines(1:length(logger$eval), logger$eval, type = "l", col = "red")
legend("topright", legend = c("train", "test"), lty=c(1,1), lwd=c(2.5,2.5), col = c("blue", "red"))

iBest = which.min(logger$eval)
print(paste0("Best iter = ", iBest, " scoring ", logger$eval[iBest]))

systole_model = mx.model.load(paste0(paste0(workingPath, "/systole"), iBest))
