    .describe("Number of inputs to be concated.");
    DMLC_DECLARE_FIELD(dim).set_range(0,  4).set_default(1)
    .describe("the dimension to be concated.");
  }
};  // struct ConcatParam
DMLC_REGISTER_PARAMETER(ConcatParam);
ConcatParam param_;
param_.Init(kwargs);
