@Override
    public Collection<String> doSharding(Collection<String> collection, RangeShardingValue<Date> rangeShardingValue) {
        Collection<String> result = new LinkedHashSet<>(collection.size());
        DateFormat sdf = new SimpleDateFormat(datePattern);
        Range<Date> ranges = rangeShardingValue.getValueRange();
        Date startTime = ranges.lowerEndpoint();
        Date endTime = ranges.upperEndpoint();

        while (startTime.getTime() <= endTime.getTime()) {
            //判断是否为当前年份，如果为当前年份则返回默认数据源
            if(new DateTime().getYear() == new DateTime(startTime).getYear()) {
                result.add(defaultDateSource);
            } else {
                result.add(namePrefix + separator + sdf.format(startTime));
            }
            //加上一年后重新设置起始时间
            startTime = new DateTime(startTime).plusYears(1).monthOfYear().withMinimumValue().dayOfMonth().withMinimumValue().secondOfDay().withMinimumValue().toDate();
        }

        if(CollectionUtils.isEmpty(result)) {
            throw new UnsupportedOperationException();
        }

        return result;
    }
