        String sql2 = "SELECT * FROM t_order WHERE content IN (?, ?) AND user_id = 1";
        setAutoCommit(false);
        getPreparedStatement(sql1, Arrays.asList("test1", "test10")).executeQuery();
        getPreparedStatement(sql2, Arrays.asList("test1", "test10")).executeQuery();
        commit();
    }
