Closing 100 prepared statements once is easier to reproduce this issue.

```java
package icu.wwj.hello.jdbc;

import lombok.SneakyThrows;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.Statement;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class MySQLProxyPreparedStatement {
    
    public static void main(String[] args) throws Exception {
        int threads = 32;
        Runnable runnable = () -> {
            for (int time = 0; time < 1000; time++) {
                try (Connection connection = getConnection(); Statement statement = connection.createStatement()) {
                    connection.setAutoCommit(false);
                    PreparedStatement[] preparedStatements = new PreparedStatement[100];
                    for (int i = 0; i < preparedStatements.length; i++) {
                        preparedStatements[i] = connection.prepareStatement("select * from bmsql_item where i_id = ?");
                        preparedStatements[i].setInt(1, i + 1);
                        try (ResultSet resultSet = preparedStatements[i].executeQuery()) {
                            while (resultSet.next()) {
                                resultSet.getInt(1);
                            }
                        }
                    }
                    for (PreparedStatement each : preparedStatements) {
                        each.close();
                    }
                    try (ResultSet resultSet = statement.executeQuery("select 1")) {
                        while (resultSet.next()) {
                            resultSet.getInt(1);
                        }
                    }
                    connection.rollback();
                } catch (Exception ex) {
                    ex.printStackTrace();
                }
            }
        };
        ExecutorService executor = Executors.newFixedThreadPool(threads);
        for (int i = 0; i < threads; i++) {
            executor.execute(runnable);
        }
        executor.shutdown();
    }
    
    @SneakyThrows
    private static Connection getConnection() {
        return DriverManager.getConnection("jdbc:mysql://127.0.0.1:13306/bmsql_sharding?useSSL=false&useServerPrepStmts=true&cachePrepStmts=false", "root", "root");
//        return DriverManager.getConnection("jdbc:mysql://127.0.0.1:3306/bmsql?useSSL=false&useServerPrepStmts=true&cachePrepStmts=false", "root", "root");
    }
}
