DataSourceConfig 应该怎样配置呢？现在数据一次取出来了。下面是我的配置文件
package com.uranus.fans.data.provider.config;

import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

import javax.sql.DataSource;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

import com.alibaba.druid.pool.DruidDataSource;
import com.dangdang.ddframe.rdb.sharding.api.ShardingDataSourceFactory;
import com.dangdang.ddframe.rdb.sharding.api.rule.DataSourceRule;
import com.dangdang.ddframe.rdb.sharding.api.rule.ShardingRule;
import com.dangdang.ddframe.rdb.sharding.api.rule.TableRule;
import com.dangdang.ddframe.rdb.sharding.api.strategy.table.TableShardingStrategy;
import com.mysql.jdbc.Driver;

@Configuration
public class DataSourceConfig {
	@Bean
	public DataSource getDataSource() {
		return buildDataSource();
	}

	private DataSource buildDataSource() {
		// 设置分库映射
		Map<String, DataSource> dataSourceMap = new HashMap<>(1);
		// 添加两个数据库ds_0,ds_1到map里
		dataSourceMap.put("uranus_fans", createDataSource("uranus_fans"));
		// 设置默认db为ds_0，也就是为那些没有配置分库分表策略的指定的默认库
		// 如果只有一个库，也就是不需要分库的话，map里只放一个映射就行了，只有一个库时不需要指定默认库，但2个及以上时必须指定默认库，否则那些没有配置策略的表将无法操作数据
		DataSourceRule dataSourceRule = new DataSourceRule(dataSourceMap);

		// 设置分表映射，将t_order_0和t_order_1两个实际的表映射到t_order逻辑表
		// 0和1两个表是真实的表，t_order是个虚拟不存在的表，只是供使用。如查询所有数据就是select * from
		// t_order就能查完0和1表的
		TableRule orderTableRule = TableRule.builder("fans").actualTables(Arrays.asList("fans_0", "fans_1", "fans_2", "fans_3", "fans_4", "fans_5", "fans_6", "fans_7", "fans_8", "fans_9"))
				.dataSourceRule(dataSourceRule).build();

		// 具体分库分表策略，按什么规则来分
		ShardingRule shardingRule = ShardingRule.builder().dataSourceRule(dataSourceRule)
				.tableRules(Arrays.asList(orderTableRule))
				.tableShardingStrategy(new TableShardingStrategy("id", new ModuloTableShardingAlgorithm()))
				.build();

		DataSource dataSource = ShardingDataSourceFactory.createDataSource(shardingRule);

		return dataSource;
	}

	/**
	 * 
	 * @param dataSourceName
	 * @return
	 */
	private static DataSource createDataSource(final String dataSourceName) {
		// 使用druid连接数据库
		DruidDataSource result = new DruidDataSource();
		result.setDriverClassName(Driver.class.getName());
		result.setUrl(String.format("jdbc:mysql://localhost:3306/%s?useUnicode=true&characterEncoding=utf8&characterSetResults=utf8", dataSourceName));
		result.setUsername("root");
		result.setPassword("wujinsong");
		return result;
	}
}

