@Component
public class ShardingDatabaseRule implements PreciseShardingAlgorithm<String> {

	
	private Logger log = LoggerFactory.getLogger(ShardingDatabaseRule.class);
	@Override
	public String doSharding(Collection<String> availableTargetNames, PreciseShardingValue<String> shardingValue) {
		log.debug("sharding begin shardingValue:{}",shardingValue);
		int bussNumHash = shardingValue.getValue().hashCode();
		int targetValue =bussNumHash  % availableTargetNames.size();
		String valueOf = String.valueOf(targetValue);
		for (String targetDB : availableTargetNames) {
			if(targetDB.endsWith(valueOf)){
				log.debug("sharding end targetDB:{}",targetDB );
				return targetDB;
			}
		}
		log.info(" end no sharding");
		return null;
	}

