void CodeGenCUDA::VisitExpr_(const Cast* op, std::ostream& os) { 
  bool fromHalf =
      op->value.type().is_float() && op->value.type().bits() == 16 && op->value.type().lanes() == 1;
  bool toint8 = (op->type.is_int() || op->type.is_uint()) && op->type.bits() == 8 && op->type.lanes() == 1;
  if (fromHalf && toint8) {
    std::stringstream value_16;
    std::stringstream value_int8;
    this->PrintExpr(op->value, value_16);
    auto inter_type = op->type.is_int() ? Int(16) : UInt(16);
    value_int8 << CastFromTo(value_16.str(), op->value.type(), inter_type);
    os << CastFromTo(value_int8.str(), inter_type, op->type);
  } else {
    CodeGenC::VisitExpr_(op, os);
  }
}
