      B.rf[ramp(0, 1, 8)] = x8(0.000000f)
      for (k.outer, 0, 16) {
        B.rf[ramp(0, 1, 8)] = (B.rf[ramp(0, 1, 8)] + A[ramp((((ax0*16) + k.outer)*8), 1, 8)])
      }
    }
    B[ax0] = 0.000000f
    for (k.inner.v, 0, 8) {
      B[ax0] = (B[ax0] + B.rf[k.inner.v])
    }
  }
}
