```
  private fun hashForId(id: SECP256K1.PublicKey): ByteArray? {
    try {
      return idHashCache.get(id) { EthereumNodeRecord.nodeId(id).toArrayUnsafe() }
    } catch (e: UncheckedExecutionException) {
      if (e.cause is IllegalArgumentException) {
        return null
      }
      throw e
    }
  }
