import org.apache.flink.api.common.eventtime.WatermarkStrategy;
import org.apache.flink.api.common.serialization.SimpleStringSchema;
import org.apache.flink.api.java.utils.ParameterTool;
import org.apache.flink.connector.kafka.source.KafkaSource;
import org.apache.flink.connector.kafka.source.KafkaSourceBuilder;
import org.apache.flink.connector.kafka.source.enumerator.initializer.OffsetsInitializer;
import org.apache.flink.streaming.api.datastream.DataStream;
import org.apache.flink.streaming.api.environment.StreamExecutionEnvironment;


import java.util.*;

public class KafkaDemo {

    public static void main(String[] args) throws Exception {
        StreamExecutionEnvironment env = StreamExecutionEnvironment.getExecutionEnvironment();
        env.getConfig().setGlobalJobParameters(ParameterTool.fromSystemProperties());
        List<String> topicNames = new ArrayList<>();
        topicNames.add("prod_db_ucontent_unipus_mobile_api_moduledata");
        KafkaSourceBuilder<String> kafkaSourceBuilder = KafkaSource.<String>builder()
                .setProperty("partition.discovery.interval.ms","6000")
                .setBootstrapServers("10.1.21.44:9092,10.1.6.141:9092,10.1.28.103:9092")
                .setGroupId("cwf-08")
                .setTopics(topicNames)
                .setStartingOffsets(OffsetsInitializer.timestamp(1655353754L))
//                .setStartingOffsets(OffsetsInitializer.latest())
                // 从时间戳大于等于指定时间的数据开始消费
                .setValueOnlyDeserializer(new SimpleStringSchema());

        KafkaSource<String> source = kafkaSourceBuilder.build();
        String sourceName = String.join( "_",topicNames);
        DataStream<String> dataStream = env.fromSource(source, WatermarkStrategy.noWatermarks(), String.format("source_"+sourceName));
        dataStream.print();
        env.execute("Fraud Detection");
    }
}
