$array_fill = "array_fill";
$array_fill(start_index: 0, count: 100, value: 50);
$array_fill(
        start_index: 0,
        count: 100,
        value: 50
);

test(test(1, 2, 3), test3: test(1, 2, 3), test2: test(1, test2: 2, test3: [1, 2, 3]));
test(
        test(1, 2, 3),
        test3: test(1, 2, 3),
        test2: test(1, test2: 2, test3: [1, 2, 3])
