        "connectURI" : "jdbc:mysql://localhost:3306/sqltest",
        "user" : "druid",
        "password" : "diurd"
        }
        },
        "sqls" : ["SELECT * FROM index_data_a", "SELECT * FROM index_data_b"],
        }
     },
     :
