    "requiredCapacity" : 1
  },
  "spec" : {
    "dataSchema" : {
      "dataSource" : "wikipedia_index_test",
      "parser" : {
        "type" : "noop"
      },
      "metricsSpec" : [ {
        "type" : "doubleSum",
        "name" : "added",
        "fieldName" : "added",
        "expression" : null
      }, {
        "type" : "longSum",
        "name" : "count",
