package com.strongduanmu.performance.instance;

import com.google.common.collect.Sets;
import org.apache.shardingsphere.infra.instance.ComputeNodeInstance;
import org.apache.shardingsphere.infra.instance.InstanceContext;
import org.apache.shardingsphere.infra.instance.definition.InstanceDefinition;
import org.apache.shardingsphere.infra.instance.definition.InstanceType;
import org.openjdk.jmh.annotations.Benchmark;
import org.openjdk.jmh.annotations.BenchmarkMode;
import org.openjdk.jmh.annotations.Fork;
import org.openjdk.jmh.annotations.Level;
import org.openjdk.jmh.annotations.Measurement;
import org.openjdk.jmh.annotations.Mode;
import org.openjdk.jmh.annotations.OutputTimeUnit;
import org.openjdk.jmh.annotations.Scope;
import org.openjdk.jmh.annotations.Setup;
import org.openjdk.jmh.annotations.State;
import org.openjdk.jmh.annotations.Threads;
import org.openjdk.jmh.annotations.Warmup;

import java.util.concurrent.TimeUnit;

/**
 * Instance context benchmark.
 */
@BenchmarkMode(Mode.Throughput)
@OutputTimeUnit(TimeUnit.MILLISECONDS)
@Fork(1)
@Threads(10)
@Warmup(iterations = 5, time = 5)
@Measurement(iterations = 5, time = 10)
@State(Scope.Benchmark)
public class InstanceContextBenchmark {
    
    private InstanceContext instanceContext;
    
    @Setup(Level.Trial)
    public void setUp() {
        instanceContext = new InstanceContext(new ComputeNodeInstance(null), null, null);
        ComputeNodeInstance instance1 = new ComputeNodeInstance(new InstanceDefinition(InstanceType.PROXY, "127.0.0.1@3307"));
        instance1.setLabels(Sets.newHashSet("OLAP", "OLTP", "CAR", "GOODS", "ORDER"));
        instanceContext.getComputeNodeInstances().add(instance1);
        ComputeNodeInstance instance2 = new ComputeNodeInstance(new InstanceDefinition(InstanceType.PROXY, "127.0.0.1@3307"));
        instance2.setLabels(Sets.newHashSet("OLAP", "OLTP"));
        instanceContext.getComputeNodeInstances().add(instance2);
        ComputeNodeInstance instance3 = new ComputeNodeInstance(new InstanceDefinition(InstanceType.PROXY, "127.0.0.1@3307"));
        instance3.setLabels(Sets.newHashSet("OLAP", "CAR"));
        instanceContext.getComputeNodeInstances().add(instance3);
        ComputeNodeInstance instance4 = new ComputeNodeInstance(new InstanceDefinition(InstanceType.PROXY, "127.0.0.1@3307"));
        instance4.setLabels(Sets.newHashSet("OLAP", "CAR"));
        instanceContext.getComputeNodeInstances().add(instance4);
        ComputeNodeInstance instance5 = new ComputeNodeInstance(new InstanceDefinition(InstanceType.PROXY, "127.0.0.1@3307"));
        instance5.setLabels(Sets.newHashSet("OLAP", "ORDER"));
        instanceContext.getComputeNodeInstances().add(instance5);
    }
    
    @Benchmark
    public void testGetComputeNodeInstanceIds() {
        instanceContext.getComputeNodeInstances(InstanceType.PROXY, Sets.newHashSet("OLAP", "CAR"));
    }
}
