realize B.rf([0, 5], [0, 1]) {
  produce B.rf {
    for (k.outer, 0, 4) {
      B.rf(k.outer, 0) =0.000000f
      for (k.inner, 0, 16) {
        B.rf(k.outer, 0) =(B.rf(k.outer, 0) + A(0, (k.inner + (k.outer*16))))
      }
    }
    B.rf(4, 0) =0.000000f
    for (k.inner, 0, 4) {
      B.rf(4, 0) =(B.rf(4, 0) + A(0, (k.inner + 64)))
