  // attr [compute(B, 0x559cdeecdfe0)] realize_scope = ""
  realize B([0, 1]) {
    produce B {
      B(0) =0.000000f
      for (k.outer.v, 0, 5) {
        B(0) =(B(0) + B.rf(k.outer.v, 0))
      }
    }
