The CUDA lrn schedule was written a long time ago and it is terribly inefficient. It is not parallelized at all in any dimension. For an input workload (1, 256, 13, 13), it results in the following kernels:

```
extern "C" __global__ void __launch_bounds__(64) default_function_kernel1(float* __restrict__ pad_data, float* __restrict__ tensor) {
  float tensor_rf[1];
  __shared__ float red_buf0[64];
  for (int ax1 = 0; ax1 < 256; ++ax1) {
    for (int ax2 = 0; ax2 < 13; ++ax2) {
      for (int ax3 = 0; ax3 < 13; ++ax3) {
        tensor_rf[(0)] = 0.000000e+00f;
        if (((int)threadIdx.x) < 5) {
          tensor_rf[(0)] = (tensor_rf[(0)] + (pad_data[(((((ax1 * 169) + (((int)threadIdx.x) * 169)) + (ax2 * 13)) + ax3))] * pad_data[(((((ax1 * 169) + (((int)threadIdx.x) * 169)) + (ax2 * 13)) + ax3))]));
        }
        __syncthreads();
        ((volatile float*)red_buf0)[(((int)threadIdx.x))] = tensor_rf[(0)];
        __syncthreads();
        if (((int)threadIdx.x) < 32) {
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 32))]);
        }
        __syncthreads();
        if (((int)threadIdx.x) < 16) {
          float w_16_0 = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 16))]);
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = w_16_0;
          float w_8_0 = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 8))]);
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = w_8_0;
          float w_4_0 = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 4))]);
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = w_4_0;
          float w_2_0 = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 2))]);
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = w_2_0;
          float w_1_0 = (((volatile float*)red_buf0)[(((int)threadIdx.x))] + ((volatile float*)red_buf0)[((((int)threadIdx.x) + 1))]);
          ((volatile float*)red_buf0)[(((int)threadIdx.x))] = w_1_0;
        }
        __syncthreads();
        tensor[((((ax1 * 169) + (ax2 * 13)) + ax3))] = ((volatile float*)red_buf0)[(0)];
      }
    }
  }
}

extern "C" __global__ void default_function_kernel2(float* __restrict__ tensor) {
  for (int ax1 = 0; ax1 < 256; ++ax1) {
    for (int ax2 = 0; ax2 < 13; ++ax2) {
      for (int ax3 = 0; ax3 < 13; ++ax3) {
        tensor[((((ax1 * 169) + (ax2 * 13)) + ax3))] = powf((2.000000e+00f + (tensor[((((ax1 * 169) + (ax2 * 13)) + ax3))] * 2.000000e-01f)), 7.500000e-01f);
      }
    }
  }
}

extern "C" __global__ void __launch_bounds__(64) default_function_kernel3(float* __restrict__ T_divide, float* __restrict__ A, float* __restrict__ tensor) {
  for (int ax1_inner = 0; ax1_inner < 4; ++ax1_inner) {
    for (int ax2 = 0; ax2 < 13; ++ax2) {
      for (int ax3 = 0; ax3 < 13; ++ax3) {
        T_divide[(((((((int)threadIdx.x) * 676) + (ax1_inner * 169)) + (ax2 * 13)) + ax3))] = (A[(((((((int)threadIdx.x) * 676) + (ax1_inner * 169)) + (ax2 * 13)) + ax3))] / tensor[(((((((int)threadIdx.x) * 676) + (ax1_inner * 169)) + (ax2 * 13)) + ax3))]);
      }
    }
  }
}

extern "C" __global__ void default_function_kernel0(float* __restrict__ pad_data, float* __restrict__ A) {
  for (int ax1 = 0; ax1 < 260; ++ax1) {
    for (int ax2 = 0; ax2 < 13; ++ax2) {
      for (int ax3 = 0; ax3 < 13; ++ax3) {
        pad_data[((((ax1 * 169) + (ax2 * 13)) + ax3))] = (((2 <= ax1) && (ax1 < 258)) ? A[(((((ax1 * 169) + (ax2 * 13)) + ax3) - 338))] : 0.000000e+00f);
      }
    }
  }
}
```

This PR replaces the old schedule with a more reasonable one, which results in the following single kernel. Since the reduction is typically over a small window ([-5, +5], for example), the reduction is simply computed sequentially. 
```
extern "C" __global__ void __launch_bounds__(1024) default_function_kernel0(float* __restrict__ A, float* __restrict__ T_divide) {
  float tensor[1];
  tensor[(0)] = 0.000000e+00f;
  for (int rxs = 0; rxs < 5; ++rxs) {
    if (((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) < 43264) {
      tensor[(0)] = (tensor[(0)] + ((((2 <= ((((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) / 169) + rxs)) && (((((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) / 169) + rxs) < 258)) ? A[(((((((int)blockIdx.x) * 1024) + (rxs * 169)) + ((int)threadIdx.x)) - 338))] : 0.000000e+00f) * (((2 <= ((((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) / 169) + rxs)) && (((((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) / 169) + rxs) < 258)) ? A[(((((((int)blockIdx.x) * 1024) + (rxs * 169)) + ((int)threadIdx.x)) - 338))] : 0.000000e+00f)));
    }
  }
  if (((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)) < 43264) {
    T_divide[(((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)))] = (A[(((((int)blockIdx.x) * 1024) + ((int)threadIdx.x)))] / powf((2.000000e+00f + (tensor[(0)] * 2.000000e-01f)), 7.500000e-01f));
  }
}
```

